import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import L from 'leaflet';
import { unsafeZones } from '../data/UnSafeZone';
import { routes } from '../data/Routes';

// Fix Leaflet icon issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const MapView = () => {
  const [map, setMap] = useState(null);

  return (
    <MapContainer center={[20.2961, 85.8245]} zoom={13} style={{ height: '100vh', width: '100%' }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; OpenStreetMap contributors'
      />
      {unsafeZones.map((zone, idx) => (
        <Marker key={idx} position={[zone.lat, zone.lng]}>
          <Popup>Safety Level: {zone.level}</Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default MapView;
