import { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

export default function MapView({ mode }) {
  useEffect(() => {
    const map = L.map("map").setView([20.2961, 85.8245], 13);

    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: "© OpenStreetMap contributors",
    }).addTo(map);

    if (mode === "unsafe") {
      L.circle([20.3001, 85.8301], { radius: 200, color: "red" }).addTo(map);
    }

    if (mode === "normal") {
      L.polyline([[20.29,85.82],[20.295,85.825],[20.3,85.83]], { color: "blue" }).addTo(map);
    }

    if (mode === "safe") {
      L.polyline([[20.29,85.82],[20.298,85.818],[20.3,85.83]], { color: "green" }).addTo(map);
    }

    return () => map.remove();
  }, [mode]);

  return <div id="map" style={{ height: "80vh" }}></div>;
}
