import { useEffect, useRef } from "react";
import mapboxgl from "mapbox-gl";
import { MAPBOX_TOKEN } from "../config";
import { unsafeZones } from "../data/unsafeZones";
import { normalRoute, safeRoute } from "../data/routes";

mapboxgl.accessToken = MAPBOX_TOKEN;

export default function MapView() {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);

  useEffect(() => {
    mapInstance.current = new mapboxgl.Map({
      container: mapRef.current,
      style: "mapbox://styles/mapbox/streets-v11",
      center: [85.8245, 20.2961], // change city if needed
      zoom: 13,
    });

    mapInstance.current.on("load", () => {
      addUnsafeZones();
      drawRoute("normal", normalRoute, "blue");
      drawRoute("safe", safeRoute, "green");
    });
  }, []);

  const addUnsafeZones = () => {
    unsafeZones.forEach(zone => {
      new mapboxgl.Marker({ color: "red" })
        .setLngLat([zone.lng, zone.lat])
        .addTo(mapInstance.current);
    });
  };

  const drawRoute = (id, coords, color) => {
    mapInstance.current.addSource(id, {
      type: "geojson",
      data: {
        type: "Feature",
        geometry: {
          type: "LineString",
          coordinates: coords,
        },
      },
    });

    mapInstance.current.addLayer({
      id,
      type: "line",
      source: id,
      paint: {
        "line-color": color,
        "line-width": 5,
      },
    });
  };

  return <div ref={mapRef} className="map-container" />;
}
